def merge_pdfs(path, output):
    # merger = PyPDF2.PdfMerger()

    # 获取所有.part结尾的文件
    files = [f for f in os.listdir(path) if '.part' in f]
    # 按照.part后的数字排序
    files.sort(key=lambda x: int(x.split('.part')[1]))
    print(files)

    # 合并所有PDF文件
    with open(output, 'wb') as outfile:
        for file in files:
            with open(os.path.join(path, file), 'rb') as infile:
                shutil.copyfileobj(infile, outfile)



